<?php require_once ('../../../../init.php');error_reporting(0);header('Content-type:text/json');$db = MySql::getInstance();if(!$_POST){exit;}$ui = array();foreach ($_POST as $key => $value){$ui[$key] = trim($value);}if(empty($ui['action'])){exit;}$printr = array();switch($ui['action']){case "like":if(!intval($ui['pid'])){print_r(json_encode(array('error'=>1)));exit;}else{$id = intval($ui['pid']);$db->query("UPDATE " . DB_PREFIX . "blog SET praise=praise+1 WHERE gid=$id");$sql = "SELECT praise FROM ".DB_PREFIX."blog WHERE gid=$id";$row = $db->fetch_array($db->query($sql));print_r(json_encode(array('error'=>0,'like'=>1,'response'=>intval($row['praise']))));exit;}break;}